insertarSolicitud.query:
	DECLARE @instructores VARCHAR(MAX);
	DECLARE @areas VARCHAR(MAX);
	DECLARE @ugacs VARCHAR(MAX);

	SET @instructores = ?;
	SET @areas = ?;
	SET @ugacs = ?;

EXEC InsertarSolicitudConInstructores
	@hora_inicio = ?,
	@hora_fin = ?,
	@tipo_competencia = ?,
	@nombre = ?,
	@total_dias = ?,
	@total_horas = ?,
	@fecha_inicio = ?,
	@fecha_fin = ?,
	@lugar = ?,
	@curso = ?,
	@numero_participantes = ?,
	@objetivo = ?,
	@alcance = ?,
	@metodologia = ?,
	@programa_evento = ?,
	@resultado_aprendizaje = ?,
	@perfil_participante = ?,
	@estatus = 1,
	@usuario_registro = ?,
	@instructores = @instructores,
	@areas = @areas,
	@ugacs = @ugacs;
